'use strict';

const properRoad = (url, roads) => {

  for (let r of roads) {
    console.log(`Checking road ${r.alias}`);
    console.log(`${r.sign.test(url)}`);
    if (r.sign.test(url)) {
      console.log(`Selected.`);
      return Promise.resolve(r);
    }
  }
  return Promise.resolve(null);

}

module.exports = properRoad;
